package io.github.ajoz.workshop.day2.solution

import io.kotlintest.specs.WordSpec

class Day2Part1SolutionTest : WordSpec() {
    init {
        "Code to the toilet in the Bunny HQ" should {
            "be 1985 for the given short instructions" {
                val instructions = """
                |ULL
                |RRDDD
                |LURDL
                |UUUUD
                """.trimMargin()

                getPart1BathroomAccessCode(instructions) shouldBe 1985
            }

            "be 84452 for the given long instructions" {
                val instructions = """
|
|
|
|
|
                """.trimMargin()

                getPart1BathroomAccessCode(instructions) shouldBe 84452
            }
        }
    }
}